// Imports
import React, { useState, useEffect } from "react";

// The Budget function
function Budget() {

  const [ total, setTotal ] = useState( 0 );

  useEffect( () => {
    fetch( "/api/total" )
      .then( res => res.json() )
      .then( data => setTotal( data ) );
  }, [] );

  return (
        <div className="section budget">
            <h3>Expense's Total</h3>
            <div>${ total }</div>
        </div>
  );
}



// Export function
export default Budget;
