// Imports
import React, { useState } from "react";
import Zoom from "@material-ui/core/Zoom";
import Fab from "@material-ui/core/Fab";
import AddIcon from "@material-ui/icons/Add";



// The Expense function returns the expense section of the website
function Expense( props ) {

  // Constants
  const [ isExpanded, setExpanded ] = useState( false );
  const [ exp, setExp ] = useState({
    title: "",
    description: ""
  });



  // Nested functions

    // The handleChange function gets current input from form
    function handleChange( event ) {

      // Get user input
      const { name, value } = event.target;

      // return user input
      setExp( prevExp => {
        return {
          ...prevExp,
          [ name ]: value
        };
      });
    }



    // The submitExp submits the info from the form to the expense array
    function submitExp( event ) {

      // Remove white space
      const isWhitespaceString = str => !str.replace(/\s/g, '').length

      // If string is not just white space
      if ( !isWhitespaceString( exp.title ) ) {

        // Add expense
        props.onAdd( exp );
        event.preventDefault();
      }

      // Else
      else {

        // Alert user to enter title
        alert( "Please enter an expense title" );
      }

      // Reset text input
      setExp({
        title:"",
        description: ""
      });
    }



    // The expand function sets isExpanded to true
    function expand() {
      setExpanded( true );
    }



  // Return expense section
  return (
    <div className="section add-expense">
        <h3>Add Expense</h3>
        <form>
          <input
            name="title"
            onClick = { expand }
            placeholder="Expense title"
            onChange={ handleChange }
            value={ exp.title }
            required
          />
          { isExpanded && (
          <textarea
            name="description"
            placeholder="Description of expense (optional)"
            onChange={ handleChange }
            value={ exp.description }
            rows={ 3 }
          />)}

          <Zoom in={ isExpanded }>
            <Fab onClick={ submitExp }>
              <AddIcon />
            </Fab>
          </Zoom>
        </form>
    </div>
  );
}



// Export function
export default Expense;
