// Imports
import React, { useState } from "react";
import Zoom from "@material-ui/core/Zoom";



// The Expense function...
function Expense( props ) {

  const [ isExpanded, setExpanded ] = useState( false );

  const [ exp, setExp ] = useState({
    title: "",
    description: ""
  });

  function handleChange( event ) {
    const { name, value } = event.target;

    setExp( prevExp => {
      return {
        ...prevExp,
        [ name ]: value
      };
    });
  }


  function submitExp( event ) {

    // Remove white space
    const isWhitespaceString = str => !str.replace(/\s/g, '').length

    // If string is not just white space
    if ( !isWhitespaceString( exp.title ) ) {

      // Add expense
      props.onAdd( exp );
      event.preventDefault();
    }

    // Else
    else {

      // Alert user to enter title
      alert( "Please enter an expense title" );
    }

    // Reset text input
    setExp({
      title:"",
      description: ""
    });
  }

  function expand() {
    setExpanded( true );
  }

  return (
    <div className="section add-expense">
        <h3>Add Expense</h3>
        <form className="create-expense">
          { isExpanded && (
          <input
            name="title"
            placeholder="Expense Title"
            onChange={ handleChange }
            value={ exp.title }
            required
          />)}
          <textarea
            name="description"
            onClick = { expand }
            placeholder="Description of expense (optional)"
            onChange={ handleChange }
            value={ exp.description }
            rows={ isExpanded ? 3 : 1 }
          />

          <Zoom in={ isExpanded }>

            <button onClick={ submitExp }>Add</button>
          </Zoom>
        </form>
    </div>
  );
}



// Export function
export default Expense;
