// Imports
import React, { useState } from "react";



// The Expense function...
function Expense( props ) {

  const [ exp, setExp ] = useState({
    title: "",
    description: ""
  });

  function handleChange( event ) {
    const { name, value } = event.target;

    setExp( prevExp => {
      return {
        ...prevExp,
        [ name ]: value
      };
    });
  }



  function submitExp( event ) {

    const isWhitespaceString = str => !str.replace(/\s/g, '').length

    if ( !isWhitespaceString( exp.title ) ) {

      props.onAdd( exp );
      event.preventDefault();

    }

    else {

      alert( "Please enter an expense title" );

    }

    setExp({
      title:"",
      description: ""
    });

  }



  return (
    <div className="section add-expense">
        <h3>Add Expense</h3>
        <form className="create-expense">
          <input
            name="title"
            placeholder="Expense Title"
            onChange={ handleChange }
            value={ exp.title }
            required
          />
          <textarea
            name="description"
            placeholder="Description of expense (optional)"
            onChange={ handleChange }
            value={ exp.description }
            rows={3}
          />
          <button onClick={ submitExp }>Add</button>
        </form>
    </div>
  );
}



// Export function
export default Expense;
